package com.fantastic.alipay.model;import android.text.TextUtils;import androidx.annotation.NonNull;import java.util.Map;/** * 支付宝官方的支付结果类 * * @author jidaojiuyou * @since 2021-05-20 */@SuppressWarnings("unused")public class PayResult {    private String resultStatus;    private String result;    private String memo;    public PayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    @NonNull    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}";    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }}